sys_script_client.script:
function onLoad() {

	var ooo = g_form.getValue('time_ooo');
	var level = g_form.getValue('e_level_resource');
	var oncall = g_form.getValue('oncall');
	var landline = g_form.getValue('landline');
	var none = g_form.getValue('none_of_the_above');

	if (ooo == 'false' && level == 'false' && oncall == 'false' && landline == 'false') {

		if (none != 'true') {

			g_form.setValue('none_of_the_above', true);
		}

		g_form.setReadOnly('none_of_the_above', true);
	}
	else {

		g_form.setReadOnly('none_of_the_above', false);
	}
}
