// cancel existing approvals
var msmeApprGR = new GlideRecord('x_elin_mobile_serv_eligibility_approval');
msmeApprGR.addQuery('approval_for', String(current.sys_id));
msmeApprGR.addQuery('active', true);
msmeApprGR.query();

while (msmeApprGR.next()) {

	msmeApprGR.state = 'cancelled';
	msmeApprGR.active = false;
	msmeApprGR.autoSysFields(false);
	msmeApprGR.update();
}

// create phone approval
