function checkIfSpecial() {

    var logTxt = '*** Eligibility Check Workflow ***\n';

    var myDivision = current.requested_for.u_division.u_name.toString().toLowerCase();
    var myTitle = current.requested_for.title.toString().toLowerCase();

    logTxt += '\nmyDivision: ' + myDivision + '\nmyTitle: ' + myTitle;

    if (myDivision.indexOf('institutional na') > -1 ||
        myDivision.indexOf('inst institutional division') > -1) {

        logTxt += '\ncalling goForMiFi';
        goForMiFi();
    }

    logTxt += '\nindex of pest: ' + myDivision.indexOf('pest');

    //************************
    // Adding these lines of code as per FRU0058744
    if (myDivision.indexOf('pest') > -1 ||
        myDivision.indexOf('qsr') > -1 ||
        myDivision.indexOf('kay') > -1 ||
        myDivision.indexOf('quick service') > -1 ||
        myDivision.indexOf('food retail') > -1 ||
        myDivision.indexOf('equipment care') > -1 ||
        myDivision.indexOf('gcs') > -1 ||
        myDivision.indexOf('eec') > -1 ||
        myDivision.indexOf('smart care') > -1) {

        logTxt += '\ncalling noneOfTheAbove';
        noneOfTheAbove();
    }

    //gs.log(logTxt, 'Eligibility Check Workflow');


    //****************************

    /*
 	************************
	// Commenting these lines of code as per FRU0058744
	
	if (myDivision.indexOf('pest elim') > -1) {
		
		// Swapped for new titles as of FRU0042675
		//if (myTitle.indexOf('training manager') > -1 || myTitle.indexOf('training representative') > -1 || myTitle.indexOf('training specialist') > -1 || myTitle.indexOf('program specialist') > -1 || myTitle.indexOf('select segment specialist') > -1 || myTitle.indexOf('select segment specialist sco') > -1 || myTitle.indexOf('service specialist') > -1 || myTitle.indexOf('service specialist ca') > -1 || myTitle.indexOf('service specialist sco') > -1 || myTitle.indexOf('service specialist trainee') > -1 || myTitle.indexOf('service specialist trainee ca') > -1 || myTitle.indexOf('sr service specialist') > -1 || myTitle.indexOf('sr service specialist ca') > -1 || myTitle.indexOf('sr service specialist sco') > -1 || myTitle.indexOf('utility service specialist') > -1 || myTitle.indexOf('utility service specialist ca') > -1 || myTitle.indexOf('remote coverage specialist') > -1) {
			
			// // New Pest titles as of FRU0042675
			if (myTitle.indexOf('sr service specialist') > -1 ||
				myTitle.indexOf('sr service specialist') > -1 ||
			myTitle.indexOf('utility service specialist') > -1 ||
			myTitle.indexOf('senior service specialist') > -1 ||
			myTitle.indexOf('service specialist') > -1 ||
			myTitle.indexOf('service specialist trainee') > -1 ||
			myTitle.indexOf('service specialist') > -1 ||
			myTitle.indexOf('service specialist trainee.') > -1 ||
			myTitle.indexOf('utility service specialist.') > -1) {
				
				noneOfTheAbove();
				
			}
		}
		
		if (myDivision.indexOf('equipment care') > -1 || myDivision.indexOf('eec') > -1 ||
			myDivision.indexOf('smart care') > -1) {
			
			if (myTitle.indexOf('assoc territory service spec') > -1 ||
				myTitle.indexOf('service technician equip care') > -1 ||
			myTitle.indexOf('master service tech equip care') > -1 ||
			myTitle.indexOf('assoc service tech equip care') > -1 ||
			myTitle.indexOf('master service tech ii') > -1 ||
			myTitle.indexOf('lead service tech equip care') > -1) {
				
				noneOfTheAbove();
			}
			
		}
 		******************************
 		*/
}


function goForMiFi() {

    current.mifi_approval = 'approved';
    current.tablet_approval = 'rejected';
    current.work_notes = 'User falls within Special User Eligibility Rules - MiFi Device Approved\n';
    current.work_notes += 'As the user has been approved for a MiFi Device, the approval for an iPad has been automatically rejected for an iPad as per policy.';


    var mifiApproval = new GlideRecord('x_elin_mobile_serv_eligibility_approval');
    mifiApproval.addQuery('approval_for', current.sys_id);
    mifiApproval.addQuery('device', 'mifi');
    mifiApproval.addQuery('state', 'requested');
    mifiApproval.addQuery('active', true);
    mifiApproval.query();

    while (mifiApproval.next()) {

        mifiApproval.comments = 'User falls within Special User Eligibility Rules - MiFi Device Approved';
        mifiApproval.state = 'approved';
        mifiApproval.update();
    }

    var ipadApproval = new GlideRecord('x_elin_mobile_serv_eligibility_approval');
    ipadApproval.addQuery('approval_for', current.sys_id);
    ipadApproval.addQuery('device', 'tablet');
    ipadApproval.addQuery('state', 'requested');
    ipadApproval.addQuery('active', true);
    ipadApproval.query();

    while (ipadApproval.next()) {

        ipadApproval.comments = 'As the user has been approved for a MiFi Device, the approval for an iPad has been automatically rejected for an iPad as per policy.';
        ipadApproval.state = 'rejected';
        ipadApproval.update();
    }
}

function noneOfTheAbove() {

    var notaLogTxt = '*** Eligibility Check Workflow: noneOfTheAbove **\n';
    notaLogTxt += '\nBEFORE';
    notaLogTxt += '\ncurrent.suppress_approval_email: ' + current.suppress_approval_email;
    notaLogTxt += '\nsuppress_end_user_email: ' + suppress_end_user_email;

    current.mifi_approval = 'rejected';
    current.tablet_approval = 'rejected';
    current.phone_approval = 'rejected';
    current.suppress_approval_email = true;
    current.suppress_end_user_email = true;
    current.work_notes = 'User is not eligibile to participate in the program - Eligibility Automatically Rejected\n';

    notaLogTxt += '\nAFTER';
    notaLogTxt += '\ncurrent.suppress_approval_email: ' + current.suppress_approval_email;
    notaLogTxt += '\nsuppress_end_user_email: ' + suppress_end_user_email;

    var mifiApproval = new GlideRecord('x_elin_mobile_serv_eligibility_approval');
    mifiApproval.addQuery('approval_for', current.sys_id);
    mifiApproval.addQuery('device', 'mifi');
    mifiApproval.addQuery('state', 'requested');
    mifiApproval.addQuery('active', true);
    mifiApproval.query();

    while (mifiApproval.next()) {

        mifiApproval.comments = 'User is not eligibile to participate in the program - MiFi Device Rejected';
        mifiApproval.state = 'rejected';
        mifiApproval.update();
    }

    var ipadApproval = new GlideRecord('x_elin_mobile_serv_eligibility_approval');
    ipadApproval.addQuery('approval_for', current.sys_id);
    ipadApproval.addQuery('device', 'tablet');
    ipadApproval.addQuery('state', 'requested');
    ipadApproval.addQuery('active', true);
    ipadApproval.query();

    while (ipadApproval.next()) {

        ipadApproval.comments = 'User is not eligibile to participate in the program - iPad Device Rejected.';
        ipadApproval.state = 'rejected';
        ipadApproval.update();
    }

    var byodApproval = new GlideRecord('x_elin_mobile_serv_eligibility_approval');
    byodApproval.addQuery('approval_for', current.sys_id);
    byodApproval.addQuery('device', 'phone');
    byodApproval.addQuery('state', 'requested');
    byodApproval.addQuery('active', true);
    byodApproval.query();

    while (byodApproval.next()) {

        byodApproval.comments = 'User is not eligibile to participate in the program - BYOD Mobile Device Rejected.';
        byodApproval.state = 'rejected';
        byodApproval.update();
    }

    notaLogTxt += '\nAt End';
    notaLogTxt += '\ncurrent.suppress_approval_email: ' + suppress_approval_email;
    notaLogTxt += '\nsuppress_end_user_email: ' + suppress_end_user_email;

    //gs.log(notaLogTxt, 'Eligibility Check Workflow: noneOfTheAbove');
}

