    while (ipadApproval.next()) {

        ipadApproval.comments = 'User is not eligibile to participate in the program - iPad Device Rejected.';
        ipadApproval.state = 'rejected';
        ipadApproval.update();
    }

    var byodApproval = new GlideRecord('x_elin_mobile_serv_eligibility_approval');
    byodApproval.addQuery('approval_for', current.sys_id);
    byodApproval.addQuery('device', 'phone');
    byodApproval.addQuery('state', 'requested');
    byodApproval.addQuery('active', true);
    byodApproval.query();

    while (byodApproval.next()) {

        byodApproval.comments = 'User is not eligibile to participate in the program - BYOD Mobile Device Rejected.';
        byodApproval.state = 'rejected';
        byodApproval.update();
    }

    notaLogTxt += '\nAt End';
    notaLogTxt += '\ncurrent.suppress_approval_email: ' + suppress_approval_email;
    notaLogTxt += '\nsuppress_end_user_email: ' + suppress_end_user_email;

    //gs.log(notaLogTxt, 'Eligibility Check Workflow: noneOfTheAbove');
}
